import React, { Component } from 'react'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { Link } from 'react-router-dom'
import { register } from '../Services/DataManipulation'

class Register extends Component {
    constructor() {
        super()
        this.state = {
            fullname: '',
            email: '',
            password: '',
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }

    onChange (e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    onSubmit (e) {
        e.preventDefault()

        const user = {
            fullname: this.state.fullname,
            email: this.state.email,
            password: this.state.password
        }

        register(user).then(res => {
            this.props.history.push(`/login`)
        })
    }

    render () {
        return (
           
     
    <div className="landing-container">
        
        <Link to="/"><div className="landing-logo"></div></Link>
        <h2 padding-bottom="true">Registration</h2>
            <form noValidate onSubmit={this.onSubmit}>
            <TextField id="email" label="E-mail" margin="normal" variant="outlined" fullWidth color="secondary" value={this.state.email} onChange={this.onChange}/>
            <TextField id="fullname" label="Full name" margin="normal" variant="outlined" fullWidth color="secondary" value={this.state.fullname} onChange={this.onChange}/>
            <TextField id="password" type="password" label="Password" margin="normal" variant="outlined" fullWidth color="secondary" value={this.state.password} onChange={this.onChange}/>
            <Button type="submit" variant="contained" color="primary" fullWidth>Register</Button>
            </form>
    </div>    
        )
    }
}

export default Register
