    import axios from 'axios';

    //Arrow function to be used for making user registration
    export const register = newUser => {
        return axios
            .post('users/register', {
                fullname: newUser.fullname,
                email: newUser.email,
                password: newUser.password,
            })
            .then(res => {
                console.log('Registered!')
            })
    }

    //Function that is using for logging in user to dashboard
    export const login = user => {
        return axios
            .post('users/login', {
                email: user.email,
                password: user.password
            })
            .then(res => {
                localStorage.setItem('usertoken', res.data)
                return res.data
            })
            .catch(err => {
                console.log(err)
            })
    }

    export const addRestaurant = newRestaurant => {
        return axios
            .post('restaurants/list', {
                name: newRestaurant.name,
                location: newRestaurant.location,
                workinghours: newRestaurant.workinghours,
            })
            .then(res => {
                console.log('Successfully added new restaurant to the list!')
            })
    }



    export const getRestaurants = restaurant => {
        return axios
            .get('restaurants/list', {
                name: restaurant.name,
                location: restaurant.location,
                workinghours: restaurant.workinghours
            })
            .then(res => {
                return res.data
            })
            .catch(err => {
                console.log(err)
            })
    }
