import React, { Component } from 'react'
import { withRouter} from 'react-router-dom'
import Fab from '@material-ui/core/Fab';
import Icon from '@material-ui/core/Icon';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';

class RestaurantsList extends Component {

    constructor() {
        super()
    }

    /*Creating DOM elements with restaurants data from database*/
    render () {
        return (
            
            <div>
                  {
                   
       
                    this.props.restaurants.map((number) =>
                    <Grid container spacing={12} key={number._id}>
                    <Grid item xs={10}>
                        {number.name}
                    </Grid>
                    <Grid item xs={2}>
                    <Fab color="primary"size="small" flex-end onClick={ this.props.createFoodOrder.bind(null, number) }>
                      <Icon>add_icon</Icon>
                    </Fab>
                    </Grid> 
                    </Grid>  
                    )
                  }


            </div>
        )
    }

    /*This function is showing a h3 tag for now, but in new update of this code there will be a panels for making food orderings*/
    creatediv(){
        
        const element = (
                <div>
                <h2 padding-bottom="true">New Restaurant</h2>
                <form noValidate>
                    <TextField
                        id="name"
                        name="name"
                        margin="normal"
                        variant="standard"
                        color="primary"
                        placeholder="Name"
                        fullWidth
                    />
                    <TextField
                        id="location"
                        name="location"
                        margin="normal"
                        variant="standard"
                        color="primary"
                        placeholder="Location"
                        fullWidth
                    />
                    <TextField
                        id="workinghours"
                        name="workinghours"
                        margin="normal"
                        variant="standard"
                        color="primary"
                        placeholder="Working Hours"
                        fullWidth
                    />
                    <Button type="submit" variant="contained" color="primary" fullWidth>Add Restaurant</Button>
                </form>
                </div> 
          );
          ReactDOM.render(element, document.getElementById('panels'));
        }
}


export default withRouter(RestaurantsList)
