    import React, { Component } from 'react'
    import Button from '@material-ui/core/Button';
    import { Link } from 'react-router-dom';
    import Navbar from './Navbar';
    import RestaurantsList from './RestaurantsList';
    import Divider from '@material-ui/core/Divider';
    import jwt_decode from 'jwt-decode'
    import { getRestaurants } from './Services'
    import Grid from '@material-ui/core/Grid';
    import TextField from '@material-ui/core/TextField';
    import ReactDOM from 'react-dom';
    import Icon from '@material-ui/core/Icon';
 
    class Dashboard extends Component {

        constructor() {
            super()
            this.state = {
                fullname: '',
                email: '',
                restaurants:[],
                openedRestaurants:[]
            }
    

        

        }

        /*If dashboard is loaded then lets save a user data to local storage for later, so we know that the user is logged in*/
        componentWillMount () {
            if(localStorage.getItem('usertoken')!==null){
            const token = localStorage.usertoken
            const decoded = jwt_decode(token)
            this.setState({
                fullname: decoded.fullname,
                email: decoded.email,
            })
        }
        
        const restaurants=
        {
            name:'',
            location: '',
            workinghours: ''
        }
        
        

        getRestaurants(restaurants).then(res => {
            if (res) {
                this.setState({restaurants:res})

               
            }
        })

        /*this.setState({restaurants:restaurants})*/
        console.log(this.state)
    }

        createFoodOrder (itemToBeDeleted) {
            if(!this.state.openedRestaurants.includes(itemToBeDeleted)){
            this.setState({ openedRestaurants: this.state.openedRestaurants.concat(itemToBeDeleted) })
        }
            console.log(this.state.openedRestaurants)
            const element = (
                <div>
                {
                    this.state.openedRestaurants.map((number) =>
                <div class="panelRestaurantItem" key={number._id}>
                <button class="restaurantPanelClose" variant="contained" color="primary">sdsds</button>
                <h2 padding-bottom="true">{number.name}</h2>
                <form noValidate>
                    <TextField
                        id="name"
                        name="name"
                        margin="normal"
                        variant="standard"
                        color="primary"
                        placeholder="Name"
                        fullWidth
                    />
                    <Button type="submit" variant="contained" color="primary" fullWidth>Add Restaurant</Button>
                </form>
                </div>
                    )}
                </div> 
          );
          ReactDOM.render(element, document.getElementById('panels'));
           
        }

        /*Render DOM elements only if user is logged in, instead of that lets prompt that user needs to be logged in to view dashboard page*/
        render () {
            if(localStorage.getItem('usertoken')!==null){
            return (
            <div><Navbar />
            <div className="sidebar">
            <h2 className="textAlignCenter bold">Restaurants</h2>
            <Divider />
            <RestaurantsList restaurants={this.state.restaurants} createFoodOrder={this.createFoodOrder.bind(this)}/>
            </div>
            <div id="panels"></div>
            </div>   
            )}else{
                return(
                    <div className="globalMessage">
                    <h1>You must me logged in to view this page!</h1> 
                    <p>Go to login page</p>
                    <Link to="/login"><Button variant="contained" color="primary">Login</Button></Link>
                    </div>
                )
            }
        }
    }



    export default Dashboard
