    import React, { Component } from 'react'
    import Button from '@material-ui/core/Button';
    import { Link } from 'react-router-dom';
    import Navbar from './Navbar';
    import RestaurantsList from './RestaurantsList';
    import Divider from '@material-ui/core/Divider';
    import jwt_decode from 'jwt-decode'
    import { getRestaurants } from './Services'


    class Dashboard extends Component {

        constructor() {
            super()
            this.state = {
                fullname: '',
                email: ''
            }

            this.restaurant = []

           this.som=[{
            "_id": "5c5b2046c34bca2cd43feafa",
            "name": "tesavuf",
            "location": "tuzla",
            "workinghours": 8,
            "__v": 0
          },
          {
            "_id": "5c5b2776b25af11ad0422897",
            "name": "gfd",
            "location": "gfdgfd",
            "workinghours": 12,
            "__v": 0
          }]
           this.setState(this.som)

        }

        //If dashboard is loaded then lets save a user data to local storage for later, so we know that the user is logged in
        componentWillMount () {
            if(localStorage.getItem('usertoken')!==null){
            const token = localStorage.usertoken
            const decoded = jwt_decode(token)
            this.setState({
                fullname: decoded.fullname,
                email: decoded.email,
            })
        }

        getRestaurants(this.restaurant).then(res => {
            if (res) {
               this.restaurant=res;
                console.log(this.restaurant)
               }
        })
           this.setState(this.restaurant)
        }

        //Render DOM elements only if user is logged in, instead of that lets prompt that user needs to be logged in to view dashboard page
        render () {
            console.log("ovo"+this.som)
            if(localStorage.getItem('usertoken')!==null){
            return (
            <div><Navbar />
            <div className="sidebar">
            <h2 className="textAlignCenter bold">Restaurants</h2>
            <Divider />
            <Restaurant restaurant={this.restaurant}/>
            </div>
            <div id="panels"></div>
            </div>   
            )}else{
                return(
                    <div className="globalMessage">
                    <h1>You must me logged in to view this page!</h1> 
                    <p>Go to login page</p>
                    <Link to="/login"><Button variant="contained" color="primary">Login</Button></Link>
                    </div>
                )
            }
        }
    }


    class Restaurant extends Component {
         myData = this.props.restaurant || {}
         
        render() {console.log(this.myData)
          return (
              <div>fhfghg</div>
          );
        }
      }

    export default Dashboard
