    import Navbar from './Navbar';
    import RestaurantsList from './RestaurantsList';
    import Divider from '@material-ui/core/Divider';
    import jwt_decode from 'jwt-decode'
    import { getRestaurants } from './Services'
    import Grid from '@material-ui/core/Grid';


    class Dashboard extends Component {

        constructor() {
            super()
            this.state = {
                fullname: '',
                email: '',
                restaurants:{
                    name: '',
                    location: '',
                    workinghours: ''
                }
            }


        

        }

        //If dashboard is loaded then lets save a user data to local storage for later, so we know that the user is logged in
        componentWillMount () {
            if(localStorage.getItem('usertoken')!==null){
            const token = localStorage.usertoken
            const decoded = jwt_decode(token)
            this.setState({
                fullname: decoded.fullname,
                email: decoded.email,
            })
        }
        const restaurants={
            name:this.state.restaurants.name,
            location: this.state.restaurants.location,
            workinghours: this.state.restaurants.workinghours
        }
