import React, { Component } from 'react'
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import TextField from '@material-ui/core/TextField';

class AddNewRestaurant extends Component {
    constructor() {
        super()
        this.state = {
            name: '',
            location: '',
            workinghours: '',
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }

    onChange (e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    onSubmit (e) {
        e.preventDefault()

        if(this.state.name!=='' && this.state.location!=='' && this.state.workinghours!==''){
                const user = {
                    fullname: this.state.fullname,
                    email: this.state.email,
                    password: this.state.password
                }
                register(user).then(res => {
                    this.props.history.push(`/login`)
                })
            }
            else{
                this.state.error="You need to type in a valid E-Mail adress."
            }
            this.setState(this.state)
    }

    render () {
        return (
            <div className="Container">
            <Navbar />
            <Link to="/"><div className="landing-logo"></div></Link>
            <h2 padding-bottom="true">Login</h2>
                <form noValidate onSubmit={this.onSubmit}>
                <TextField id="email" name="email" label="E-mail" margin="normal" variant="outlined" fullWidth color="secondary" value={this.state.email} onChange={this.onChange} />
                <TextField id="password" name="password" type="password" label="Password" margin="normal" variant="outlined" fullWidth color="secondary" value={this.state.password} onChange={this.onChange} />
                <Button type="submit" variant="contained" color="primary" fullWidth>Login</Button>
                </form>
                <div className="errorMessage">{this.state.error}</div>
        </div>    
            )
        
    }
}

export default AddNewRestaurant
