import React, { Component } from 'react'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { Link } from 'react-router-dom'
import { login } from './DataManipulation'

class Login extends Component {
    constructor() {
        super()
        this.state = {
            email: '',
            password: '',
            error:''
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }

    onChange (e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    onSubmit (e) {
        e.preventDefault()
        if(this.state.email=='' && this.state.password==''){
            this.state.error="You need to type in all the informations!"}
            else{
                const user = {
                    email: this.state.email,
                    password: this.state.password
                }
        
                login(user).then(res => {
                    if (res) {
                        this.props.history.push('/dashboard')
                    }
                })
            }
            this.setState(this.state)
    }

    render () {
        return (

    <div className="landing-container">
        
        <Link to="/"><div className="landing-logo"></div></Link>
        <h2 padding-bottom="true">Login</h2>
            <form noValidate onSubmit={this.onSubmit}>
            <TextField id="email" name="email" label="E-mail" margin="normal" variant="outlined" fullWidth color="secondary" value={this.state.email} onChange={this.onChange} />
            <TextField id="password" name="password" type="password" label="Password" margin="normal" variant="outlined" fullWidth color="secondary" value={this.state.password} onChange={this.onChange} />
            <Button type="submit" variant="contained" color="primary" fullWidth>Login</Button>
            </form>
            <h2>{this.state.error}</h2>
    </div>    
        )
    }
}

export default Login
